var PORT = 3000;

var options = {};
var fs = require('fs');
var routes = require('./routes');
var express = require('express');
var app = express();
var http = require('http');
var server = http.createServer(app);
// Подключаем модуль и ставим на прослушивание 3000-порта
var io = require('socket.io').listen(server, options);


var site_url = "http://sanc";
app.use(function (req, res, next) {
    res.setHeader("Access-Control-Allow-Origin", site_url);
    res.header("Access-Control-Allow-Methods", "GET, POST, PUT, DELETE");
    res.header("Access-Control-Allow-Headers", "Content-Type");
    res.setHeader('Access-Control-Allow-Credentials', true);
    next();
});

app.get('/TestChat', routes.TestChat);

server.listen(PORT);

// Отключаем вывод полного лога
io.set('log level', 1);


// Навешиваем обработчик на подключение нового клиента
io.sockets.on('connection', function (socket) {
    var time = (new Date).toLocaleTimeString();
    // Посылаем клиенту сообщение о том, что он успешно подключился и имя оператора + фотку + должность
    socket.json.send({
        'event': 'init',
        'name': 'Мария',
        'post': 'менеджер отдела продаж',
        'avatar': 'data:image/png;base64,', 'time': time
    });

    // Навешиваем обработчик на входящее сообщение
    socket.on('message', function (msg) {
        var time = (new Date).toLocaleTimeString();
        // Уведомляем клиента, что его сообщение успешно дошло до сервера
        socket.json.send({ 'event': 'send_message-u', 'text': msg, 'time': time });
        // Посылаем ответ бота
        socket.json.send({ 'event': 'send_message-s', 'text': 'Я Чат бот, готов ответить на любой Ваш вопрос', 'time': time });
    });
    // Отключение клиента
    /**
    *Про события close_chat/show_chat - не совсем понял, disconnect актуален при отключение одного из пользователей чата, можно другим написать, что например
    *Вася - отключился. А у нас по сути если клиент отключился то оператор узнает об этом, если срабатывает событие disconnect
    *
    *События набора сообщение акруально при общении друг с другом, логика работы на примере с ботом не совсем понятно. Если только эмулировать,
    * что типа оператор набирает сообщение
    */
    socket.on('disconnect', function () {
        //var time = (new Date).toLocaleTimeString();
        //io.sockets.json.send({ 'event': 'close_chat', 'time': time });
    });
});